		'params', "obj, context",
		'code', function (self, obj, context)
			local units = empty_table
			local group = Groups[self.TargetUnit] 
			if group then
				local unitClass = {}
				for i, obj in ipairs(group) do
					if IsKindOf(obj, "Unit") then
						unitClass[#unitClass + 1] = obj
					end
