			if #self.Banters == 0 then
				return "Add at least one banter"
			end
			if self.searchInMarker and self.searchInMap then
				return "Don't select both search in marker and search in map."
			end
		end,
	}),
	PlaceObj('PropertyDefPresetIdList', {
