	}),
	PlaceObj('ClassMethodDef', {
		'name', "__exec",
		'params', "obj, context",
		'code', function (self, obj, context)
			local valid = {}
			for i, eff in ipairs(self.Effects) do
				if EvalConditionList(eff.Conditions, obj, context) then
					valid[#valid + 1] = eff
				end
