PlaceObj('TestCombat', {
	DisplayText = T(751881737486, --[[TestCombat Test AnimTestCombat DisplayText]] "_Animation Test"),
	OnMapLoaded = function (self)
		g_VoiceResponsesEnabled = false
		g_SnapCameraEnabled = false
		g_UnitCombatBadgesEnabled = false
		
		CreateMapRealTimeThread(function()
			while GetInGameInterfaceMode() ~= "IModeDeployment" and GetInGameInterfaceMode() ~= "IModeExploration" and GetInGameInterfaceMode() ~= "IModeCombatMovement" do
				Sleep(20)
			end
			print("start mode: ", GetInGameInterfaceMode())
			if GetInGameInterfaceMode() == "IModeDeployment" then
				print("starting exploration...")
				Dialogs.IModeDeployment:StartExploration()
				while GetInGameInterfaceMode() == "IModeDeployment" do
					Sleep(10)
				end
				print("exploration started")
			end
			if GetInGameInterfaceMode() == "IModeExploration" then
				print("starting combat...")
				NetSyncEvent("ExplorationStartCombat")
				while GetInGameInterfaceMode() ~= "IModeCombatMovement" do
					Sleep(10)
				end
				print("combat started")
			end
				
			local side = GetPoVTeam().side
			--NetSyncEvent("CheatEnable", "GodMode", true, side)
			NetSyncEvent("CheatEnable", "FullVisibility", true)
			NetSyncEvent("CheatEnable", "WeakDamage", true) -- To be possible Pain preview of the enemies
			
			printf("num units: %d", #g_Units)
			for _, unit in ipairs(g_Units) do
				if unit.team.side ~= "player1" then
					unit.dummy = true
				end
			end
		end)
	end,
	group = "Test",
	id = "AnimTestCombat",
	map = "_AnimTest",
	skip_deployment = true,
	squads = {
		PlaceObj('TestCombatSquad', nil),
	},
	trigger_enemy_spawners = {
		"enemy",
	},
})

